// 选择占卜类别
function selectCategory(category) {
    selectedCategory = category;
}

function saveInput() {
    const a = document.getElementById("numA").value;
    const b = document.getElementById("numB").value;
    const c = document.getElementById("numC").value;

    localStorage.setItem('numA', a);
    localStorage.setItem('numB', b);
    localStorage.setItem('numC', c);
}

function calculate() {
    const a = document.getElementById("numA").value;
    const b = document.getElementById("numB").value;
    const c = document.getElementById("numC").value;

    // 检查是否为空
    if (!a || !b || !c) {
        alert("请输入三组数字");
        return;
    }

    // 验证是否为3位数（100-999）
    const numA = parseInt(a);
    const numB = parseInt(b);
    const numC = parseInt(c);

    if (numA < 100 || numA > 999 || numB < 100 || numB > 999 || numC < 100 || numC > 999) {
        alert("请输入三位数字（100-999）");
        return;
    }

    // 保存输入值
    saveInput();

    let low = parseInt(a) % 8 || 8;
    let up = parseInt(b) % 8 || 8;
    let change = parseInt(c) % 6 || 6;
    const key = `${up}${low}`;
    const name = guaNames[key] || "易经卦象";
    const fullDetail = (interpretations[key] && interpretations[key][change]) ? interpretations[key][change] : "【占解】当前处境正在转化。时运：稳健为上；财运：宜守待机；家宅：和气致祥；身体：注意劳逸。";

    // 提取选中类别的解释
    const detail = extractCategoryDetail(fullDetail, selectedCategory);

    const allLines = [...baguaLines[low], ...baguaLines[up]];
    let guaHtml = [...allLines].reverse().map((isYang, index) => {
        const currentYao = 6 - index;
        const color = (currentYao === change) ? "#ff4d4d" : "#d4af37";
        return isYang ? `<div class="line-yang" style="background:${color}"></div>` : `<div class="line-yin"><div style="background:${color}"></div><div style="background:${color}"></div></div>`;
    }).join('');

    const resultHtml = `<div style="margin-bottom:15px">${guaHtml}</div><strong style="font-size:24px; color:#d4af37">${name}</strong><p style="color:#ff4d4d; margin:10px 0; font-weight:bold;">【变爻：第 ${change} 爻】</p><div style="text-align:center; border-top:1px dashed #555; padding-top:15px; font-size:17px; line-height:2; color:#f0e68c;"><strong style="color:#d4af37;">占卜结果：</strong>${detail}</div>`;
    document.getElementById("result").innerHTML = resultHtml;
}

// 从完整解释中提取指定类别的内容
function extractCategoryDetail(fullDetail, category) {
    // 解释格式：①时运：xxx。②财运：xxx。③家宅：xxx。④身体：xxx。
    const categoryMap = {
        '时运': '①时运：',
        '财运': '②财运：',
        '家宅': '③家宅：',
        '身体': '④身体：'
    };

    const categoryPrefix = categoryMap[category];
    if (!categoryPrefix) return fullDetail;

    const startIndex = fullDetail.indexOf(categoryPrefix);
    if (startIndex === -1) return fullDetail;

    // 找到下一个类别的开始位置（②、③、④或字符串结尾）
    let endIndex = fullDetail.length;
    const nextMarkers = ['②', '③', '④'];

    for (let marker of nextMarkers) {
        const nextIndex = fullDetail.indexOf(marker, startIndex + categoryPrefix.length);
        if (nextIndex !== -1 && nextIndex < endIndex) {
            endIndex = nextIndex;
        }
    }

    // 提取内容
    let content = fullDetail.substring(startIndex + categoryPrefix.length, endIndex).trim();

    // 移除末尾的句号（如果有）
    if (content.endsWith('。')) {
        content = content.slice(0, -1);
    }

    return content;
}

